generated.css:
*{
  box-sizing: border-box;
}
body {
  margin: 0;
  padding: 0;
  font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", "Roboto", "Oxygen",
    "Ubuntu", "Cantarell", "Fira Sans", "Droid Sans", "Helvetica Neue",
    sans-serif;
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
}

ul {
  list-style-type: none;
}

.nav-container{
    display: flex;
    justify-content: space-between;
    align-items: center;
    border-bottom: 1px solid rgb(196, 196, 196);
    height: 40px;
}
.side-container{
  display: flex;
  justify-content: space-between;
  width: 150px;
  margin-right: 50px;
}
.content-container{
  display: flex;
  justify-content: space-around;

}

.shop-list-label{
  text-align: right;
}

.item-container{
    margin-bottom: 20px;
    background-color: #fff;
    border: 1px solid #ff7800;
    border-radius: 4px;
    box-shadow: 0 1px 1px rgba(0,0,0,.05);
}

.item-header{
    display: flex;
    justify-content: space-between;
    padding: 10px 15px;
    border: 3px;
    border-bottom: 1px solid transparent;
    color: #fff;
    background-color: #ff7800;
    border-color: #ff7800;
    border-radius: 4px 4px 0 0;
}

#create-space{
  padding-right: 20px;
}

.item-description{  
  padding: 15px;
}

.item-footer{
  padding: 10px 15px;
  background-color: #f5f5f5;
  border-top: 1px solid rgb(168, 154, 154);
  border-bottom-right-radius: 3px;
  border-bottom-left-radius: 3px;
}

#modal {
  background-color: rgba(0, 0, 0, 0.9);
  position: fixed;
  left: 0;
  right: 0;
  bottom: 0;
  top: 0;
  z-index: 10;
  display: flex;
  justify-content: center;
  align-items: center;
}

#modal:empty {
  display: none;
}

#modal > div {
  background-color: white;
  max-width: 500px;
  padding: 15px;
  border-radius: 5px;
  text-align: center;
}

.modal-content{
  display: flex;
  align-items: center;
  flex-direction: column;
}

input, textarea{
  border-radius: 4px;
  line-height: 1.5em;
  width: 100%;
}

.center-content{
  margin-top: 20%;
}
